import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);

  const fetchTickerTape = async () => {
    try {
      const { data } = await axios.get(Tape());
      setTicker(data);
    } catch (error) {
      console.error('Error fetching ticker tape data:', error);
    }
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  const tapeCoinIds = ['bitcoin', 'ethereum', 'binancecoin', 'ripple', 'cardano', 'solana', 'tron', 'polkadot'];

  const tapeCoins = ticker.filter(coin => tapeCoinIds.includes(coin.id));

  const items = tapeCoins.map(coin => (
    <Link to={`/coins/${coin.id}`} key={coin.id}>
      <img src={coin?.image} alt={coin.name} height='30' />
    </Link>
  ));

  useEffect(() => {
    const tickerContainer = document.querySelector('.ticker');
    const tickerItems = document.querySelectorAll('.ticker-item');
    let distance = 0;

    const moveTicker = () => {
      distance--;
      tickerContainer.style.transform = `translateX(${distance}px)`;

      if (distance <= -tickerItems[0].offsetWidth) {
        distance += tickerItems[0].offsetWidth;
        tickerContainer.appendChild(tickerItems[0]);
      }
    };

    const tickerInterval = setInterval(moveTicker, 20); // Adjust the interval duration as needed

    return () => clearInterval(tickerInterval);
  }, []);

  return (
    <div className="ticker-container">
      <div className="ticker">
        {items}
      </div>
    </div>
  );
};

export default TickerTape;
