import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';
import './TickerTape.css'; // Import your CSS file

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);
  const tickerRef = useRef(null);

  const fetchTickerTape = async () => {
    const { data } = await axios.get(Tape());
    setTicker(data);
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  useEffect(() => {
    // Clone the ticker items and append them to create a loop effect
    if (tickerRef.current) {
      tickerRef.current.innerHTML = tickerRef.current.innerHTML + tickerRef.current.innerHTML;
    }
  }, [ticker]);

  return (
    <div className="ticker-container">
      <div className="ticker" ref={tickerRef}>
        {ticker.map(coin => (
          <Link to={`/coins/${coin.id}`} key={coin.id}>
            <img src={coin?.image} alt={coin.name} height='30' />
          </Link>
        ))}
      </div>
    </div>
  );
};

export default TickerTape;
