import React, { useState, useEffect } from 'react';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';
import './tickerTape.css';

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);

  const fetchTickerTape = async () => {
    try {
      const response = await fetch(Tape());
      if (!response.ok) {
        throw new Error('Failed to fetch ticker data');
      }
      const data = await response.json();
      setTicker(data);
    } catch (error) {
      console.error('Error fetching ticker data:', error);
    }
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  const tapeCoinIds = ['bitcoin', 'ethereum', 'binancecoin', 'ripple', 'cardano', 'solana', 'tron', 'polkadot'];

  const tapeCoins = ticker.filter(coin => tapeCoinIds.includes(coin.id));

  const items = tapeCoins.map(coin => (
    <Link to={`/coins/${coin.id}`} key={coin.id}>
      <img src={coin?.image} alt={coin.name} height='30' />
    </Link>
  ));

  // Clone the items and append them at the end of the list
  const clonedItems = [...items];

  return (
    <div className="ticker-container">
      <div className="ticker">
        {clonedItems}
        {items}
        {clonedItems}
      </div>
    </div>
  );
};

export default TickerTape;
