import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);
  const tickerContainerRef = useRef(null);
  const tickerRef = useRef(null);
  const itemWidthRef = useRef(0);

  const fetchTickerTape = async () => {
    const { data } = await axios.get(Tape());
    setTicker(data);
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  useEffect(() => {
    if (tickerContainerRef.current && tickerRef.current) {
      const tickerContainerWidth = tickerContainerRef.current.offsetWidth;
      const tickerWidth = tickerRef.current.offsetWidth;
      itemWidthRef.current = tickerWidth / ticker.length;

      if (tickerContainerWidth < tickerWidth) {
        // Duplicate the items to create a seamless loop
        tickerRef.current.innerHTML += tickerRef.current.innerHTML;

        // Start the animation loop
        animateTicker();
      }
    }
  }, [ticker]);

  const animateTicker = () => {
    if (tickerContainerRef.current && tickerRef.current) {
      const tickerContainerWidth = tickerContainerRef.current.offsetWidth;
      const tickerWidth = tickerRef.current.offsetWidth;
      const animationDuration = (tickerWidth / ticker.length) * 500; // Adjust the animation speed

      tickerRef.current.style.animation = `ticker-slide ${animationDuration}ms linear infinite`;
      tickerRef.current.style.width = `${tickerWidth}px`;

      // Reset animation after one loop
      setTimeout(() => {
        tickerRef.current.style.animation = 'none';
        setTimeout(() => {
          tickerRef.current.style.animation = `ticker-slide ${animationDuration}ms linear infinite`;
        }, 10);
      }, animationDuration);
    }
  };

  const filteredCoins = ticker.filter(coin =>
    ['bitcoin', 'ethereum', 'binancecoin', 'ripple', 'cardano', 'solana', 'tron', 'polkadot'].includes(coin.id)
  );

  return (
    <div className="ticker-container" ref={tickerContainerRef}>
      <div className="ticker" ref={tickerRef}>
        {filteredCoins.map(coin => (
          <Link to={`/coins/${coin.id}`} key={coin.id}>
            <img src={coin?.image} alt={coin.name} height='30' width={itemWidthRef.current} />
          </Link>
        ))}
      </div>
    </div>
  );
};

export default TickerTape;
