import React, { useState, useEffect } from 'react';
import { CoinList } from '../config/api.js';
import Link from 'react-router-dom';



const Main = () => {

    const [coins, setCoins] = useState([]);
    const [search, setSearch] = useState('');

    const fetchCoins = async () => {
        try {
        const response = await fetch(CoinList());
        if (response.ok) {
            const data = await response.json();
            setCoins(data);
        } else {
            console.error('Failed to fetch ticker tape data');
        }
        } catch (error) {
        console.error('An error occurred while fetching data:', error);
        }
    };

  useEffect(() => {
    fetchCoins();
  }, []);

  const handleSearch = () => {
    return coins.filter((coin) => (
        coin.name.toLowerCase().includes(search) || coin.symbol.toLowerCase().includes(search)
    ))
  }

  return (
    <div className="main-container">
        <div className="search">
            <h2>Search a coin</h2>
            <form>
                <input type="text" 
                placeholder='Search' 
                className='coin-input' 
                onChange={(e) => setSearch (e.target.value)} />
            </form>
        </div>
        <div className="coin-list">
            <div className="coin-header">
                <p>#</p>
                <p>Coin</p>
                <p>24h %</p>
                <p>24h <span>Volume</span></p>
                <p>Market cap</p>
            </div>
            <div className="coin-row">
            {handleSearch.map(coin => (
                <Link to={`/${coin.id}`} key={coin.id} className="ticker-item">
                <div className='ticker-items'>
                    <img src={coin?.image} alt={coin.name} />
                    {coin.symbol}
                    <div className={`positive-negative ${coin.price_change_percentage_24h < 0 ? 'negative' : 'positive'}`}>
                    ${coin.current_price.toFixed(2)}
                    <span className="price-change">
                    {coin.price_change_percentage_24h.toFixed(2)}%
                    </span>
                    </div>
                </div>
                </Link>
            ))}
            </div>
        </div>
    </div>
  )
}

export default Main
