import React, { useState, useEffect } from 'react';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';
import './TickerTape.css';

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);

  const fetchTickerTape = async () => {
    try {
      const response = await fetch(Tape());
      if (response.ok) {
        const data = await response.json();
        setTicker(data);
      } else {
        console.error('Failed to fetch ticker tape data');
      }
    } catch (error) {
      console.error('An error occurred while fetching data:', error);
    }
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  const handpickedCoinIds = ['bitcoin', 'ethereum', 'binancecoin', 'cardano', 'solana'];

  const handpickedCoins = ticker.filter(coin => handpickedCoinIds.includes(coin.id));

  const animationDuration = handpickedCoins.length * 10;

  return (
    <div className="ticker-container">
      <div className="ticker-content" style={{ animationDuration: `${animationDuration}s` }}>
        {handpickedCoins.map(coin => (
          <Link to={`/coins/${coin.id}`} key={coin.id} className="ticker-item">
            <img src={coin?.image} alt={coin.name} />
            {coin.name}
          </Link>
        ))}
        {/* Duplicate the items to create the seamless loop */}
        {handpickedCoins.map(coin => (
          <Link to={`/coins/${coin.id}`} key={`loop-${coin.id}`} className="ticker-item">
            <img src={coin?.image} alt={coin.name} />
            {coin.name}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default TickerTape;
