import React, { useState, useEffect } from 'react';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';
import './TickerTape.css'; // Import your CSS file for styling

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  const fetchTickerTape = async () => {
    try {
      const response = await fetch(Tape());
      if (!response.ok) {
        throw new Error('Failed to fetch ticker data');
      }
      const data = await response.json();
      setTicker(data);
    } catch (error) {
      console.error('Error fetching ticker data:', error);
    }
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  useEffect(() => {
    const animationInterval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % tapeCoins.length);
    }, 3000); // Adjust the interval as needed

    return () => clearInterval(animationInterval);
  }, [ticker]);

  const tapeCoinIds = ['bitcoin', 'ethereum', 'binancecoin', 'ripple', 'cardano', 'solana', 'tron', 'polkadot'];

  const tapeCoins = ticker.filter(coin => tapeCoinIds.includes(coin.id));

  const items = tapeCoins.map(coin => (
    <Link to={`/coins/${coin.id}`} key={coin.id}>
      <img src={coin?.image} alt={coin.name} height='30' />
    </Link>
  ));

  return (
    <div className="ticker-container">
      <div className="ticker">
        <div
          className="ticker-items"
          style={{
            transform: `translateX(-${currentIndex * 30}px)` // Adjust the width of each item as needed
          }}
        >
          {items}
          {items} {/* Duplicate items to create the illusion of a loop */}
        </div>
      </div>
    </div>
  );
};

export default TickerTape;
