import React, { useState, useEffect } from 'react';
import _ from 'lodash';
import { CoinList } from '../config/api.js';
import { Link } from 'react-router-dom';
import Coin from './Coin.jsx';

const Main = () => {
  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const coinsPerPage = 10;

  const fetchCoins = async () => {
    try {
      const response = await fetch(CoinList());
      if (response.ok) {
        const data = await response.json();
        setCoins(data);
      } else {
        console.error('Failed to fetch ticker tape data');
      }
    } catch (error) {
      console.error('An error occurred while fetching data:', error);
    }
  };

  const fetchCoinsThrottled = _.throttle(fetchCoins, 60000);

  useEffect(() => {
    fetchCoinsThrottled();
  }, []);

  const filteredCoins = coins.filter((coin) => (
    coin.name.toLowerCase().includes(search.toLowerCase()) || coin.symbol.toLowerCase().includes(search.toLowerCase())
  ));

  const indexOfLastCoin = currentPage * coinsPerPage;
  const indexOfFirstCoin = indexOfLastCoin - coinsPerPage;
  const currentCoins = filteredCoins.slice(indexOfFirstCoin, indexOfLastCoin);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(filteredCoins.length / coinsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <div className="main-container">
      <div className="search">
        <h2>Search</h2>
        <form>
          <input
            type="text"
            className="coin-input"
            onChange={(e) => setSearch(e.target.value)}
          />
        </form>
      </div>
      <div className="coin-list">
        <div className="coin-header">
          {/* ... existing header code ... */}
        </div>
        <div className="coin-row">
          {currentCoins.map((coin) => {
            return (
              <Link to={`${coin.id}`} key={coin.id}>
                <Coin
                  key={coin.id}
                  rank={coin.market_cap_rank}
                  image={coin.image}
                  name={coin.name}
                  symbol={coin.symbol}
                  change={coin.price_change_percentage_24h}
                  volume={coin.total_volume}
                  price={coin.current_price}
                  mcap={coin.market_cap}
                />
              </Link>
            );
          })}
        </div>
      </div>
      <ul className="pagination">
        {pageNumbers.map((number) => (
          <li key={number} className="page-item">
            <a
              onClick={() => paginate(number)}
              href="#"
              className={`page-link ${currentPage === number ? 'active' : ''}`}
            >
              {number}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Main;
