import React, { useState, useEffect, useRef } from 'react';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);
  const [offset, setOffset] = useState(0);
  const tickerRef = useRef(null);

  const fetchTickerTape = async () => {
    try {
      const response = await fetch(Tape());
      if (!response.ok) {
        throw new Error('Failed to fetch ticker data');
      }
      const data = await response.json();
      setTicker(data);
    } catch (error) {
      console.error('Error fetching ticker data:', error);
    }
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  const tapeCoinIds = ['bitcoin', 'ethereum', 'binancecoin', 'ripple', 'cardano', 'solana', 'tron', 'polkadot'];

  const tapeCoins = ticker.filter(coin => tapeCoinIds.includes(coin.id));

  useEffect(() => {
    const interval = setInterval(() => {
      setOffset(prevOffset => (prevOffset + 1) % tapeCoins.length);
    }, 3000); // Adjust the interval as needed (3000ms = 3 seconds)

    return () => clearInterval(interval);
  }, [tapeCoins.length]);

  const items = tapeCoins.map(coin => (
    <Link to={`/coins/${coin.id}`} key={coin.id} className="ticker-item">
      <img src={coin?.image} alt={coin.name} height='30' />
    </Link>
  ));

  useEffect(() => {
    const tickerWidth = tickerRef.current.offsetWidth;
    tickerRef.current.style.transform = `translateX(-${offset * tickerWidth}px)`;
  }, [offset]);

  return (
    <div className="ticker-container">
      <div className="ticker" ref={tickerRef}>
        {items}
      </div>
    </div>
  );
};

export default TickerTape;
