import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);
  const tickerContainerRef = useRef(null);

  const fetchTickerTape = async () => {
    try {
      const { data } = await axios.get(Tape());
      setTicker(data);
    } catch (error) {
      console.error('Error fetching ticker tape data:', error);
    }
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  const tapeCoinIds = ['bitcoin', 'ethereum', 'binancecoin', 'ripple', 'cardano', 'solana', 'tron', 'polkadot'];

  const tapeCoins = ticker.filter(coin => tapeCoinIds.includes(coin.id));

  const items = tapeCoins.map(coin => (
    <Link to={`/coins/${coin.id}`} key={coin.id}>
      <img src={coin?.image} alt={coin.name} height='30' />
    </Link>
  ));

  useEffect(() => {
    const tickerContainer = tickerContainerRef.current;
    let distance = 0;

    const moveTicker = () => {
      distance -= 1;
      tickerContainer.style.transform = `translateX(${distance}px)`;

      if (distance <= -tickerContainer.offsetWidth / 2) {
        distance += tickerContainer.offsetWidth / 2;
        tickerContainer.appendChild(tickerContainer.firstElementChild);
      }

      requestAnimationFrame(moveTicker);
    };

    const animationFrameId = requestAnimationFrame(moveTicker);

    return () => cancelAnimationFrame(animationFrameId);
  }, []);

  return (
    <div className="ticker-container">
      <div className="ticker" ref={tickerContainerRef}>
        {items}
      </div>
    </div>
  );
};

export default TickerTape;
