import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);

  const fetchTickerTape = async () => {
    const { data } = await axios.get(Tape());
    setTicker(data);
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  // Define an array of coin IDs that you want to display
  const handpickedCoinIds = ['bitcoin', 'ethereum', 'binancecoin', 'cardano', 'solana'];

  const handpickedCoins = ticker.filter(coin => handpickedCoinIds.includes(coin.id));

  const items = handpickedCoins.map(coin => (
    <Link to={`/coins/${coin.id}`} key={coin.id}>
      <img src={coin?.image} alt={coin.name} />
    </Link>
  ));

  return <div className="ticker-container">{items}</div>;
};

export default TickerTape;
