import React, { useState, useEffect, useRef } from 'react';
import { Tape } from '../config/api.js';
import { Link } from 'react-router-dom';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);
  const slider = useRef(null);

  const fetchTickerTape = async () => {
    try {
      const response = await fetch(Tape());
      if (response.ok) {
        const data = await response.json();
        setTicker(data);
      } else {
        console.error('Failed to fetch ticker tape data');
      }
    } catch (error) {
      console.error('An error occurred while fetching data:', error);
    }
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  const handpickedCoinIds = ['bitcoin', 'ethereum', 'binancecoin', 'cardano', 'solana'];

  const handpickedCoins = ticker.filter(coin => handpickedCoinIds.includes(coin.id));

  const settings = {
    infinite: true,
    speed: 500, // Adjust the speed as needed
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 0,
    cssEase: 'linear',
    pauseOnHover: true,
    swipeToSlide: false,
    beforeChange: (currentSlide, nextSlide) => {
      if (nextSlide === handpickedCoins.length) {
        setTimeout(() => {
          slider.current.slickGoTo(0);
        }, 0);
      }
    },
    onReInit: () => {
      if (slider.current) {
        slider.current.slickGoTo(0);
      }
    },
  };

  return (
    <div className="ticker-container">
      <Slider ref={slider} {...settings}>
        {handpickedCoins.map(coin => (
          <Link to={`/coins/${coin.id}`} key={coin.id} className="ticker-item">
            <img src={coin?.image} alt={coin.name} />
          </Link>
        ))}
      </Slider>
    </div>
  );
};

export default TickerTape;
