import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './TickerTape.css';

const TickerTape = () => {
  const [ticker, setTicker] = useState([]);
  const tickerRef = useRef(null);

  // Simulate fetching data
  const fetchTickerTape = async () => {
    // Replace this with your actual data fetching logic
    const data = await fetchData(); // Your data fetching function
    setTicker(data);
  };

  useEffect(() => {
    fetchTickerTape();
  }, []);

  const handpickedCoinIds = ['bitcoin', 'ethereum', 'binancecoin', 'cardano', 'solana'];

  const handpickedCoins = ticker.filter(coin => handpickedCoinIds.includes(coin.id));

  useEffect(() => {
    const tickerContainer = tickerRef.current;
    const itemWidth = tickerContainer.offsetWidth / handpickedCoins.length;

    // Duplicate items for seamless loop
    tickerContainer.innerHTML += tickerContainer.innerHTML;

    const moveTicker = () => {
      tickerContainer.style.transform = `translateX(-${itemWidth}px)`;
      tickerContainer.style.transition = 'transform 0.5s ease-in-out';

      setTimeout(() => {
        tickerContainer.style.transition = 'none';
        tickerContainer.style.transform = 'translateX(0)';
        setTimeout(moveTicker, 10); // Delay before starting the next loop iteration
      }, 500); // Adjust this delay according to your desired speed
    };

    moveTicker();
  }, [handpickedCoins]);

  return (
    <div className="ticker-wrapper">
      <div ref={tickerRef} className="ticker-container">
        {handpickedCoins.map(coin => (
          <Link to={`/coins/${coin.id}`} key={coin.id} className="ticker-item">
            <img src={coin?.image} alt={coin.name} />
          </Link>
        ))}
      </div>
    </div>
  );
};

export default TickerTape;
